<style>

  #hello-path {
    position: relative;
    width: 440px;
    z-index: 6;


    animation-name: wipe;
    animation-duration: .01s;
    animation-delay: 1.1s;
    opacity: 0;
    animation-fill-mode: forwards;
    animation-play-state: paused;
  }

  :global(#hello.animate #hello-path) {
    animation-play-state: running;
  }

  #hello-wrap {
    display: inline-block;
    position: relative;
    overflow: hidden;
/*    margin: 40px 0 0 40px;*/
  }

  #hello-wrap svg {}

  @keyframes wipe {
    0% {
      opacity: 0;
    }
    50% {
      opacity: 0;
    }
    51% {
      opacity: 1;
    }
    100% {
      opacity: 1;
    }
  }

  #hello-bg {
    background: white;
    width: 0%;
    position: absolute;
    top: 0;
/*    left: 0;*/
    bottom: 0;
/*    right: 0;*/
    z-index: 5;

/*    transition: all 0.2s ease-in-out;*/

  }


</style>
